settings.custom_liquid:
{% if product.metafields.custom.sizing_chart != blank %}
  {% assign sizing_chart_value = product.metafields.custom.sizing_chart.value %}
  {% assign chart_id = 'size-chart-modal-' | append: product.id %}

  {% if product.metafields.custom.sizing_chart.type == 'list.file_reference' %}
    {% for media_id in sizing_chart_value %}
      {% assign media = media_id | media_object %}
      {% if media %}
        <button class="size-chart-trigger" data-modal-id="{{ chart_id }}">
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="ruler-icon">
            <path d="M3 21h18M3 7V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2v2M5 21V3M9 21v-4M13 21v-4M17 21v-4M19 21V3"/>
          </svg>
          <span>Size Chart</span>
        </button>

        <!-- Modal -->
        <div id="{{ chart_id }}" class="custom-modal">
          <div class="custom-modal-content">
            <div class="custom-modal-header">
              <h5>Size Chart</h5>
              <span class="custom-modal-close">&times;</span>
            </div>
            <div class="custom-modal-body">
              <img src="{{ media.preview_image | img_url: '1000x' }}" alt="Size Chart">
            </div>
          </div>
        </div>
        {% break %}
      {% endif %}
    {% endfor %}

  {% elsif product.metafields.custom.sizing_chart.type == 'file_reference' %}
    {% assign media = sizing_chart_value | media_object %}
    {% if media %}
      <button class="size-chart-trigger" data-modal-id="{{ chart_id }}">
        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="ruler-icon">
          <path d="M3 21h18M3 7V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2v2M5 21V3M9 21v-4M13 21v-4M17 21v-4M19 21V3"/>
        </svg>
        <span>Size Chart</span>
      </button>

      <!-- Modal -->
      <div id="{{ chart_id }}" class="custom-modal">
        <div class="custom-modal-content">
          <div class="custom-modal-header">
            <h5>Size Chart</h5>
            <span class="custom-modal-close">&times;</span>
          </div>
          <div class="custom-modal-body">
            <img src="{{ media.preview_image | img_url: '1000x' }}" alt="Size Chart">
          </div>
        </div>
      </div>
    {% endif %}
  {% endif %}

<style>
  /* Size Chart Button Styles */
  .size-chart-trigger {
    display: inline-flex;
    align-items: center;
    padding: 8px 12px;
    background: none;
    border: 1px solid #ddd;
    border-radius: 4px;
    cursor: pointer;
    margin: 10px 0;
    font-size: 14px;
  }

  .size-chart-trigger .ruler-icon {
    margin-right: 8px;
  }

  /* Modal Styles */
  .custom-modal {
    display: none;
    position: fixed;
    z-index: 9999;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    overflow: auto;
    padding: 20px;
  }

  .custom-modal-content {
    background-color: #fff;
    margin: 5% auto;
    padding: 20px;
    border-radius: 5px;
    max-width: 800px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    position: relative;
  }

  .custom-modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
    border-bottom: 1px solid #eee;
    padding-bottom: 10px;
  }

  .custom-modal-header h5 {
    margin: 0;
    font-size: 18px;
  }

  .custom-modal-close {
    font-size: 24px;
    font-weight: bold;
    cursor: pointer;
  }

  .custom-modal-body {
    text-align: center;
  }

  .custom-modal-body img {
    max-width: 100%;
    height: auto;
  }
</style>

<script>
  document.addEventListener('DOMContentLoaded', function() {
    // Modal functionality
    const triggers = document.querySelectorAll('.size-chart-trigger');

    triggers.forEach(trigger => {
      trigger.addEventListener('click', function() {
        const modalId = this.getAttribute('data-modal-id');
        const modal = document.getElementById(modalId);

        if (modal) {
          modal.style.display = 'block';

          // Close when clicking the X
          const closeBtn = modal.querySelector('.custom-modal-close');
          if (closeBtn) {
            closeBtn.addEventListener('click', function() {
              modal.style.display = 'none';
            });
          }

          // Close when clicking outside the modal content
          window.addEventListener('click', function(event) {
            if (event.target === modal) {
              modal.style.display = 'none';
            }
          });
        }
      });
    });
  });
</script>
{% endif %}
